mapper.select:
SELECT clubno, adminno, clubname, player, headcoach, legend, history, info, rank
FROM club
WHERE clubno=#{clubno}

mapper.update:
UPDATE club 
SET clubname=#{clubname}, player=#{player}, headcoach=#{headcoach}, legend=#{legend}, history=#{history}, info=#{info}, rank=#{rank} 
WHERE clubno=#{clubno}
