mapper.select:
SELECT surveyno, survey_title, start_date, fin_date, y_n, cnt, poster, postersaved, posterthumb, postersize
FROM survey
WHERE surveyno=#{surveyno}

mapper.update:
UPDATE survey 
SET survey_title=#{survey_title}, start_date=#{start_date}
WHERE surveyno=#{surveyno}

mapper.select:
SELECT COUNT(*) as cnt FROM survey
WHERE y_n=#{y_n}
WHERE y_n=#{y_n} AND (UPPER(survey_title) LIKE '%' || UPPER(#{word}) || '%')
