mapper.select:
SELECT surveyno, survey_title, start_date, fin_date, y_n, cnt, poster, postersaved, posterthumb, postersize, recom
FROM survey
WHERE surveyno=#{surveyno}

mapper.update:
UPDATE survey 
SET survey_title=#{survey_title}, start_date=#{start_date}
WHERE surveyno=#{surveyno}

UPDATE survey
SET cnt = cnt + 1
WHERE surveyno = #{surveyno}
