mapper.select:
SELECT surveyno, survey_title, seqno, qa_num, qa_contents, start_date, fin_date, write_date, adminno, memberno
FROM survey
WHERE surveyno=#{surveyno}

mapper.update:
UPDATE survey 
SET qa_contents=#{qa_contents}, start_date=#{start_date}, fin_date=#{fin_date} 
WHERE surveyno=#{surveyno}
